CREATE TABLE `comments` ( `id` INTEGER PRIMARY KEY AUTOINCREMENT, `table_name` TEXT, `col_name` TEXT, `col_comment` TEXT )
CREATE TABLE `message` ( `id` INTEGER PRIMARY KEY AUTOINCREMENT, `user_id` INTEGER, `message` TEXT, `status` INTEGER DEFAULT 0 )
CREATE TABLE "room" ( `id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` TEXT NOT NULL, `desc` TEXT, `group_id` INTEGER, `ctime` TEXT, `utime` TEXT )
CREATE TABLE "room_group" ( `id` INTEGER PRIMARY KEY AUTOINCREMENT, `name` TEXT NOT NULL, `desc` TEXT )
CREATE TABLE sqlite_sequence(name,seq)
CREATE TABLE "user" ( `id` INTEGER PRIMARY KEY AUTOINCREMENT, `open_id` TEXT NOT NULL UNIQUE, `nick_name` TEXT, `avatar` TEXT, `gender` TEXT, `language` TEXT, `city` INTEGER, `province` INTEGER, `country` INTEGER, `real_name` INTEGER, `stu_no` TEXT, `status` INTEGER DEFAULT 0, `desc` TEXT, `ctime` TEXT, `utime` TEXT )
CREATE TABLE "user_room_appoint" ( `id` INTEGER PRIMARY KEY AUTOINCREMENT, `user_id` INTEGER NOT NULL, `room_id` INTEGER NOT NULL, `date` TEXT NOT NULL, `start` INTEGER NOT NULL, `end` INTEGER NOT NULL, `status` INTEGER NOT NULL DEFAULT 1, `ctime` TEXT, `utime` TEXT )
CREATE INDEX `message_index` ON `message` ( `user_id`, `status` )
CREATE INDEX `user_index` ON `user` ( `open_id` )
CREATE INDEX `user_room_appoint_index` ON `user_room_appoint` ( `user_id`, `room_id`, `date` )